import { Dropdown } from 'flowbite';
import React, { ChangeEvent, useEffect, useRef, useState } from 'react';
import DropdownButton from './components/op/DropdownButton';
import DropdownSelect from './components/op/DropdownSelect';

function App() {

    const targetRef = useRef(null);
    const triggerRef = useRef(null);

    useEffect(() => {
        new Dropdown(targetRef.current, triggerRef.current, {
            placement: 'bottom',
            triggerType: 'click',
            offsetSkidding: 0,
            offsetDistance: 5,
            delay: 300,
            ignoreClickOutsideClass: false
        });
    })

    const [options, setOptions] = useState([
        {label: 'Choice 1', value: false},
        {label: 'Choice 2', value: true},
        {label: 'Choice 3', value: true},
        {label: 'Choice 4', value: true},
    ])

    const handleClick = (label: string) => {
        const isOptionClicked = (element: any) => element.label === label;
        const index = options.findIndex(isOptionClicked);

        if (index >= 0) {
            const copy = [...options];
            copy[index].value = !copy[index].value
            setOptions(copy)
        }
    }


    return (
        <body className='w-screen h-screen flex items-center justify-center'>
            <div className='w-36'>
               <DropdownSelect options={options} onClick={handleClick}/>
            </div>
        </body>
    );
}

export default App;
