import { Dropdown } from 'flowbite';
import React, { useEffect, useRef } from 'react';
import DropdownButton from './DropdownButton';


interface DropdownSelectProps  {
    onChange?: (event: Event) => void;
    options: {label: string, value: boolean}[]
}

const DropdownSelect: React.FC<DropdownSelectProps> = ({options, onChange}) => {

    const targetRef = useRef(null);
    const triggerRef = useRef(null);

    useEffect(() => {
        new Dropdown(targetRef.current, triggerRef.current, {
            placement: 'bottom',
            triggerType: 'click',
            offsetSkidding: 0,
            offsetDistance: 5,
            delay: 300,
            ignoreClickOutsideClass: false
        });
    })

    return (
        <DropdownButton title='select'>
            <ul className="p-3 space-y-3 text-sm text-gray-700 dark:text-gray-200" aria-labelledby="dropdownCheckboxButton">
                {
                    options.map((option, key) => (
                        <li key={key}>
                            <div className="flex items-center">
                                <input onChange={onChange} id={`checkbox-${key}`} type="checkbox" checked={option.value} className="w-4 h-4 text-blue-600 bg-gray-100 border-gray-300 rounded focus:ring-blue-500 dark:focus:ring-blue-600  "/>
                                <label className="ml-2 text-sm font-medium text-gray-900 dark:text-gray-300">{option.label}</label>
                            </div>
                        </li> 
                    ))
                }

            </ul>
        </DropdownButton>
    );
}

export default DropdownSelect;
