import React from "react";

interface TagProps {
    color: 'blue' | 'green' | 'orange' | 'red';
    children?: React.ReactNode;
}

const Tag: React.FC<TagProps> = ({children, color}) => {
    const colors = {
        "green": "bg-green-200 text-green-500  border-green-500",
        "red": "bg-red-200 text-red-500  border-red-500",
        "blue": "bg-blue-200 text-blue-500  border-blue-500",
        "orange": "bg-orange-200 text-orange-500  border-orange-500",
    }

    return <>
        <span className={"px-3.5 py-1 border rounded-xl text-sm flex" + colors[color]} >
            {children}
        </span>    
    </>
}

export default Tag;
