import { Dropdown } from 'flowbite';
import React, { useEffect, useRef } from 'react';

interface Props {
    children?: React.ReactNode;
    title: string;
}

const DropdownButton: React.FC<Props> = ({children, title}) => {
    const targetRef = useRef(null);
    const triggerRef = useRef(null);

    useEffect(() => {
        new Dropdown(targetRef.current, triggerRef.current, {
            placement: 'bottom',
            triggerType: 'click',
            offsetSkidding: 0,
            offsetDistance: 5,
            delay: 300,
            ignoreClickOutsideClass: false
        });
    })

    return (
        <>
            <button ref={triggerRef} id="dropdownOffsetButton" data-dropdown-toggle="dropdownOffset" data-dropdown-offset-distance="35" data-dropdown-offset-skidding="0" className="text-white bg-orange-500 hover:bg-orange-400 focus:ring-4 focus:outline-none font-medium rounded-lg text-l px-6 py-2.5 text-center inline-flex items-center w-full " type="button">
                { title }
                <svg className="w-2.5 h-2.5 ml-2.5" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 6 10">
                    <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="m1 9 4-4-4-4"/>
                </svg>
            </button>
            <div ref={targetRef} id="dropdownOffset" className="z-10 hidden bg-white divide-y divide-gray-100 rounded-lg shadow w-44 dark:bg-gray-700">
                { children }
            </div>
        </>
    )
}

export default DropdownButton;
