import React, { useCallback } from 'react';
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
} from 'reactflow';

import 'reactflow/dist/style.css';
import { NavBar } from './components/NavBar';
import { Button } from 'flowbite-react';

const initialNodes = [
  { id: '1', position: { x: 0, y: 0 }, data: { label: '1' } },
  { id: '2', position: { x: 0, y: 100 }, data: { label: '2' } },
];
const initialEdges = [{ id: 'e1-2', source: '1', target: '2' }];

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);

  const onConnect = useCallback((params: any) => setEdges((eds) => addEdge(params, eds)), [setEdges]);

  return (

    <div className='h-screen w-screen'>
        <div className='h-[64px] sticky top-0 z-20 w-full border-b bg-[#1e2227] text-white flex items-center px-12'>
            Navbar
        </div>
        <div className='h-[64px] sticky top-0 z-20 w-full border-b bg-white flex items-center px-12 justify-between'>
            <button className='text-gray-800 hover:text-orange-500 flex items-center font-semibold'>
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-4 h-4 mr-2">
                    <path strokeLinecap="round" strokeLinejoin="round" d="M15.75 19.5L8.25 12l7.5-7.5" />
                </svg>
                Revenir à la page scénario
            </button>


            <div className='flex space-x-6 items-center'>

                <div className='flex'>
                    <button className='p-2 border rounded-lg rounded-r-none hover:text-orange-500'>
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-4 h-4">
                            <path strokeLinecap="round" strokeLinejoin="round" d="M9 15L3 9m0 0l6-6M3 9h12a6 6 0 010 12h-3" />
                        </svg>
                    </button>
                    <button className='p-2 border rounded-lg rounded-l-none hover:text-orange-500'>
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-4 h-4">
                            <path strokeLinecap="round" strokeLinejoin="round" d="M15 15l6-6m0 0l-6-6m6 6H9a6 6 0 000 12h3" />
                        </svg>

                    </button>
                </div>
                <hr className="w-px h-8 mx-auto my-8 bg-gray-300 border-0 rounded md:my-12" />

                <button className='bg-white text-gray-800 border px-6 py-1.5 flex items-center rounded-lg'>
                    Editer le scénario
                </button>
                <button className='bg-orange-500 text-white px-6 py-1.5 flex items-center rounded-lg'>
                    Sauvegarder
                </button>

            </div>

        </div>
        <div className='h-[calc(100vh-128px)] w-screen'>
            <ReactFlow
                nodes={nodes}
                edges={edges}
                onNodesChange={onNodesChange}
                onEdgesChange={onEdgesChange}
                onConnect={onConnect}
            >
                <Controls />
                <MiniMap />
                <Background gap={12} size={1} />
            </ReactFlow>
        </div>
    </div>
  );
}
