import React from "react";

interface SelectOption {
    label: string;
    value: string;
}

interface SelectProps {
    label?: string;
    id?: string;
    selectedOptionLabel?: string;
    options: SelectOption[];
    onSelect?: (value: string) => void;
}

const Select: React.FC<SelectProps> = ({label, selectedOptionLabel, id, options, onSelect}) => {

    return <>
        {
            label && (
                <label className="block mb-2 text-sm font-medium text-gray-900">
                    { label }
                </label>
            )
        }
        <select
            className="bg-white border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5"
            id={id}
            onChange={(event) => onSelect ? onSelect(event.target.value) : undefined}
        >
            {
                options.map(({label, value}, key) => {
                    return (
                        <option key={key} selected={label === selectedOptionLabel} value={value}>
                            { label }
                        </option>
                    )
                })
            }
        </select>
    </>
}

export default Select;
