import { Drawer } from "flowbite";
import React, { useEffect, useRef, useState } from "react";


interface CustomDrawerProps {
    isOpen: boolean;
    onClose: () => void;
    children?: React.ReactNode;
}

const CustomDrawer: React.FC<CustomDrawerProps> = ({isOpen, onClose, children}) => {
    const targetRef = useRef(null);
    const [drawer, setDrawer] = useState<Drawer | null>(null)

    useEffect(() => {

        if (drawer === null) {
            const instance = new Drawer(targetRef.current,  {
                placement: 'right',
                backdrop: true,
                bodyScrolling: false,
                edge: false,
                edgeOffset: '',
                backdropClasses: 'bg-gray-900 bg-opacity-50 dark:bg-opacity-80 fixed inset-0 z-30',
            })
            setDrawer(instance)
        }
        if (isOpen)
            drawer?.show();
        else
            drawer?.hide();
    }, [isOpen, drawer])

    return <>
        <div ref={targetRef} id="drawer-right-example" className="fixed top-0 right-0 z-40 h-screen p-4 overflow-y-auto transition-transform translate-x-full bg-white w-80 dark:bg-gray-800" tabIndex={-1} aria-labelledby="drawer-right-label">
           {children}
        </div>
    </>
}


function App() {
    const [isDrawerOpen, setIsDrawerOpen] = useState(false);
    const closeDrawer = () => setIsDrawerOpen(false)

    return (
        <body className='w-screen h-screen  p-12'>
            <div className="text-center">
                <button onClick={() => setIsDrawerOpen(false)} className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 mr-2 mb-2 dark:bg-blue-600 dark:hover:bg-blue-700 focus:outline-none dark:focus:ring-blue-800" type="button" data-drawer-target="drawer-right-example" data-drawer-show="drawer-right-example" data-drawer-placement="right" aria-controls="drawer-right-example">
                    Show right drawer
                </button>
            </div>

        <CustomDrawer onClose={closeDrawer} isOpen={isDrawerOpen} />
        </body>
    );
}

export default App;
