import React from "react";

interface TextAreaProps {
    label?: string;
    id?: string;
    placeholder?: string;
    onChange?: (value: string) => void;
    rows?: number;
    value?: string;
}

const TextArea: React.FC<TextAreaProps> = ({label, id, placeholder, onChange, rows}) => {
    return <>
        {
            label && (
                <label className="block mb-2 text-sm font-medium text-gray-900">
                    { label }
                </label>
            )
        }
        <textarea
            id={id}
            rows={rows}
            className="block p-2.5 w-full text-sm text-gray-900 bg-gray-50 rounded-lg border border-gray-300 focus:ring-orange-500 focus:border-orange-500"
            placeholder={placeholder}
            onChange={(event) => onChange ? onChange(event.target.value) : undefined}
            value={value}
        />
    </>
}

export default TextArea;
