import React, { useCallback, useMemo, useState } from "react";

interface AvatarProps {
    fullName: string;
    job: string
    src: string
}

const Avatar: React.FC<AvatarProps> = ({fullName, job, src}) => {
    return <div className="space-x-6 hidden md:flex">
        <img className="w-10 hidden md:block h-10 rounded-full" src={src} alt="Rounded avatar"/>
        <div className="flex-col">
            <p className="text-white font-semibold">{fullName}</p>
            <p className="text-gray-400 text-sm hidden md:block">{job}</p>
        </div>
    </div>
}

interface NavLink {
    path: string;
    label: string;
}

interface NavBarProps {
    links: NavLink[]
    user: {
        fullName: string;
        job: string;
        profilePicture: string;
    }
}
export const NavBar: React.FC<NavBarProps> = ({links, fullName, profilePicture}) => {

    const [onMobileMenu, setOnMobileMenu] = useState(false);
    const toggleMobileMenu = useCallback(() => {
        setOnMobileMenu((isOpen) => !isOpen)
    }, [])

    return (
        <>
            {
                onMobileMenu &&
                (
                    <div className="z-0 fixed h-screen w-screen bg-[#1e2227] text-white flex flex-col items-center justify-center space-y-6">
                    {
                        links.map((link, key) => {
                            return <button key={key} className="py-1.5 px-6 hover:bg-[#5f5f5f] text-white rounded-lg text-2xl">
                                {link.label}
                            </button>
                        })
                    }
                    </div>
                )

            }
            <div className='h-[64px] sticky top-0 z-20 w-full border-b bg-[#1e2227] flex items-center px-12 justify-between'>

                <div className="h-full items-center flex w-fit">
                    <h1 className="font-semibold uppercase text-white">HEADER</h1>
                    <hr className="  w-px h-8 mx-6 my-8 border-0 rounded bg-gray-500"/>
                    <div className="space-x-6 hidden md:flex">
                        {
                            links.map((link, key) => {
                                return <button key={key} className="py-1.5 px-6 hover:bg-[#5f5f5f] text-white rounded-lg">
                                    {link.label}
                                </button>
                            })
                        }
                    </div>
                </div>

                <div className="items-center space-x-6">
                    <button onClick={toggleMobileMenu} className="flex md:hidden rounded-full h-8 w-8 text-center  justify-content-center items-center text-gray-300 hover:bg-gray-700">
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6 mx-auto">
                            <path strokeLinecap="round" strokeLinejoin="round" d="M3.75 5.25h16.5m-16.5 4.5h16.5m-16.5 4.5h16.5m-16.5 4.5h16.5" />
                        </svg>
                    </button>
                    <Avatar fullName={fullName} job={job} src={profilePicture}/>
                </div>

            </div>
        </>
    )
}
