import React from "react";

interface SearchFieldProps {
    id?: string;
    placeholder?: string;
    onChange?: (value: string) => void;
}

const SearchField: React.FC<SearchFieldProps> = ({id, placeholder, onChange}) => {
    return <>
        <label className="mb-2 text-sm font-medium text-gray-900 sr-only">{id}</label>
        <div className="relative">
            <div className="absolute inset-y-0 left-0 flex items-center pl-3 pointer-events-none">
                <svg className="w-4 h-4 text-gray-500" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 20 20">
                    <path stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" stroke-width="2" d="m19 19-4-4m0-7A7 7 0 1 1 1 8a7 7 0 0 1 14 0Z"/>
                </svg>
            </div>
            <input
                type="search"
                id={id}
                className="block w-full py-1.5 pl-10 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 focus:ring-orange-500 focus:border-orange-500 "
                placeholder={placeholder}
                onChange={(event) => onChange ? onChange(event.target.value) : undefined}
            />
        </div>
    </>
}

export default SearchField;
