import ApexCharts from "apexcharts";
import React, { useEffect, useState } from "react";

interface Serie {
    name: string;
    color: string;
    data: {x: string | number, y: string | number}
}

interface ChartProps {
    series: Serie[]
}

const Chart: React.FC<ChartProps> = ({series}) => {
    const ref = React.useRef(null);
    const [loaded, setLoaded] = useState<boolean>(false);
    const [options, setOptions] = useState(
        {
            series: series ,
            chart: {
              type: "bar",
              fontFamily: "Inter, sans-serif",
              toolbar: {
                show: false,
              },
              height: "250px"
            },
            plotOptions: {
              bar: {
                horizontal: false,
                columnWidth: "70%",
                borderRadiusApplication: "end",
                borderRadius: 12,
              },
            },
            grid: {
              show: false,
              strokeDashArray: 4,
              padding: {
                left: 2,
                right: 2,
                top: -14
              },
            },
            dataLabels: {
              enabled: false,
            },
            legend: {
              show: false,
            },
            xaxis: {
              labels: {
                show: true,
                style: {
                  fontFamily: "Inter, sans-serif",
                  cssClass: 'text-xs font-normal fill-gray-500 dark:fill-gray-400'
                }
              },
              axisBorder: {
                show: false,
              },
              axisTicks: {
                show: false,
              },
            },
            yaxis: {
              show: false,
            },
            fill: {
              opacity: 1,
            },
        }
    )
    useEffect(() => {
        const chart = new ApexCharts(ref.current, options)
        chart.render()
    }, [])

    return (
        <div id="column-chart" ref={ref} key={options.series.length}>
        </div>
    )
}

const Dashboard = () => {

    return <div className="p-6">
        <div className="w-full md:w-[50vw]  bg-white rounded-lg shadow  p-4 md:p-6 border">

            <div className="w-full flex flex-wrap justify-between">
                <h1 className="text-xl dark:bg-gray-800 font-semibold ">Overview</h1>

                <div className="bg-white border border-gray-300 rounded-lg flex py-1 px-2 items-center mt-2 md:mt-0">
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M6.75 3v2.25M17.25 3v2.25M3 18.75V7.5a2.25 2.25 0 012.25-2.25h13.5A2.25 2.25 0 0121 7.5v11.25m-18 0A2.25 2.25 0 005.25 21h13.5A2.25 2.25 0 0021 18.75m-18 0v-7.5A2.25 2.25 0 015.25 9h13.5A2.25 2.25 0 0121 11.25v7.5" />
                    </svg>
                    <select id="countries" className=" text-gray-900 text-sm border-transparent block w-36 py-1">
                        <option selected>Cette Semaine</option>
                        <option value="US">Ce Mois</option>
                        <option value="CA">Cette Ann√©e</option>
                    </select>
                </div>
            </div>
            <div className="mt-6 flex justify-between items-center">
                <div className="flex space-x-6">
                    <dl className="flex-col items-center mr-12">
                        <dd className="text-gray-900 text-2xl dark:text-white font-semibold">$3,232</dd>
                        <dt className="text-gray-500 dark:text-gray-400 text-sm font-normal mr-1">Week</dt>
                    </dl>
                    <dl className="flex-col items-center justify-end">
                        <dd className="text-gray-900 text-2xl dark:text-white font-semibold">1.2%</dd>
                        <dt className="text-gray-500 dark:text-gray-400 text-sm font-normal mr-1">Conversion rate</dt>
                    </dl>
                </div>



            </div>

            <Chart/>
        </div>
    </div>
}

export default Dashboard;
