import { Dropdown } from 'flowbite';
import React, { useEffect, useRef } from 'react';

function App() {

    const targetRef = useRef(null);
    const triggerRef = useRef(null);

    useEffect(() => {
        new Dropdown(targetRef.current, triggerRef.current, {
            placement: 'bottom',
            triggerType: 'click',
            offsetSkidding: 0,
            offsetDistance: 5,
            delay: 300,
            ignoreClickOutsideClass: false
        });
    })

    return (
        <body>
            <div  className='h-screen w-screen bg-slate-50'>
                <button ref={triggerRef} id="dropdownCheckboxButton" data-dropdown-toggle="dropdownDefaultCheckbox" className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center inline-flex items-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800" type="button">
                    Dropdown checkbox
                    <svg className="w-2.5 h-2.5 ml-2.5" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 10 6">
                        <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="m1 1 4 4 4-4"/>
                    </svg>
                </button>
            <div  ref={targetRef} id="dropdownDefaultCheckbox" className="z-10 hidden w-48 bg-white divide-y divide-gray-100 rounded-lg shadow dark:bg-gray-700 dark:divide-gray-600">

            </div>
            </div>
        </body>
    );
}

export default App;
