import { Drawer } from "flowbite";
import React, { useEffect, useRef, useState } from "react";

interface CustomDrawerProps {
    isOpen: boolean;
    children?: React.ReactNode;
}

const options = {
    placement: 'right',
    backdrop: true,
    bodyScrolling: false,
    edge: false,
    edgeOffset: '',
    backdropClasses: 'bg-gray-900 bg-opacity-50 dark:bg-opacity-80 fixed inset-0 z-30',
}

const CustomDrawer: React.FC<CustomDrawerProps> = ({isOpen, children}) => {
    const targetRef = useRef(null);
    const [drawer, setDrawer] = useState<Drawer | null>(null)

    useEffect(() => {
        if (drawer === null) {
            const instance = new Drawer(targetRef.current, options)
            setDrawer(instance)
        }
        if (isOpen)
            drawer?.show();
        else
            drawer?.hide();
    }, [isOpen, drawer])

    return <>
        <div ref={targetRef} id="drawer-right-example" className="fixed top-0 right-0 z-40 h-screen p-4 overflow-y-auto transition-transform translate-x-full bg-white  " tabIndex={-1} aria-labelledby="drawer-right-label">
           {children}
        </div>
    </>
}

export default CustomDrawer;
