  return (
    <div className='h-screen w-screen'>
      <header>
        <NavBar />
      </header>
      <body>
          <div className=''>
            <DndContext>
              <Draggable>
                DRAGGABLE
              </Draggable>
              <Droppable>
                <div className='w-64 h-64 border-2'>
                  drop zone
                </div>
              </Droppable>
            </DndContext>
          </div>
      </body>
    </div>
  );
}

export default App;
