import { Dropdown } from 'flowbite';
import React, { useEffect, useRef } from 'react';
import DropdownButton from './components/op/DropdownButton';

function App() {

    const targetRef = useRef(null);
    const triggerRef = useRef(null);

    useEffect(() => {
        new Dropdown(targetRef.current, triggerRef.current, {
            placement: 'bottom',
            triggerType: 'click',
            offsetSkidding: 0,
            offsetDistance: 5,
            delay: 300,
            ignoreClickOutsideClass: false
        });
    })

    return (
        <body className='w-screen h-screen flex items-center'>

            <div className='w-36'>
                <DropdownButton title='select'>
                    <ul className="p-3 space-y-3 text-sm text-gray-700 dark:text-gray-200" aria-labelledby="dropdownCheckboxButton">
                        <li>
                            <div className="flex items-center">
                                <input id="checkbox-item-1" type="checkbox" value="" className="w-4 h-4 text-blue-600 bg-gray-100 border-gray-300 rounded focus:ring-blue-500 dark:focus:ring-blue-600 dark:ring-offset-gray-700 dark:focus:ring-offset-gray-700 focus:ring-2 dark:bg-gray-600 dark:border-gray-500"/>
                                <label className="ml-2 text-sm font-medium text-gray-900 dark:text-gray-300">Default checkbox</label>
                            </div>
                        </li>
                        <li>
                            <div className="flex items-center">
                                <input checked id="checkbox-item-2" type="checkbox" value="" className="w-4 h-4 text-blue-600 bg-gray-100 border-gray-300 rounded focus:ring-blue-500 dark:focus:ring-blue-600 dark:ring-offset-gray-700 dark:focus:ring-offset-gray-700 focus:ring-2 dark:bg-gray-600 dark:border-gray-500"/>
                                <label className="ml-2 text-sm font-medium text-gray-900 dark:text-gray-300">Checked state</label>
                            </div>
                        </li>
                    </ul>
                </DropdownButton>
            </div>
        </body>
    );
}

export default App;
