import { useState } from "react";
import SearchField from "../Input/SearchField";

export interface Column<Row> {
    label: string;
    render: (row: Row) => JSX.Element | string
    key?: keyof Row
}

interface DataTableProps<T> {
    columns: Column<T>[]
    rows: T[]
}

function Datatable<T>({rows, columns}: DataTableProps<T>) {

    const [searchedRows, setSearchedRows] = useState(rows);

    const onSearchQuery = (query: string) => {
        if (query === '')
            setSearchedRows(rows)

        const filteredRows: T[] = [];

        for (const row of rows) {
            for (const column of columns) {

                const value = (column.key ? row[column.key] : '') as string;

                if (value.includes(query) && !filteredRows.includes(row)) {
                    filteredRows.push(row)
                }
            }
        }
        setSearchedRows(filteredRows);
    }

    return (
        <>
            <div className="text-xl font-semibold p-3.5 border flex justify-between items-center rounded-tr-lg rounded-tl-lg">
                <div className="w-96">
                    <SearchField placeholder="Rechercher une ressource..." onChange={onSearchQuery}/>
                </div>

                <div className="text-sm font-semibold">
                    {searchedRows.length} RÃ©sultats
                </div>
            </div>
            <div className="relative overflow-x-auto shadow-md ">
                <table className="w-full text-sm text-left text-gray-500 ">
                    <thead className="text-xs text-[#a9b3c0] uppercase bg-[#fdfdfd]  border">
                        <tr>
                            <th scope="col" className="p-4" >
                                <div className="flex items-center">
                                    <input id="checkbox-all-search" type="checkbox" className="w-4 h-4 text-orange-600 bg-gray-100 border-gray-300 rounded focus:ring-orange-500  focus:ring-2 "/>
                                    <label className="sr-only">checkbox</label>
                                </div>
                            </th>
                            {
                                columns.map((column) => (
                                    <th scope="col" className="px-6 py-3">
                                        {column.label}
                                    </th>
                                ))
                            }
                        </tr>
                    </thead>
                    <tbody>
                        {
                            searchedRows.map((row, index) => {
                                return (
                                    <tr key={index} className="bg-white border-b">
                                        <td className="w-4 p-4">
                                            <div className="flex items-center">
                                                <input  type="checkbox" className="w-4 h-4 text-orange-600 bg-gray-100 border-gray-300 rounded focus:ring-orange-500 dark:focus:ring-orange-600 dark:ring-offset-gray-800 dark:focus:ring-offset-gray-800 focus:ring-2 dark:bg-gray-700 dark:border-gray-600"/>
                                                <label className="sr-only">checkbox</label>
                                            </div>
                                        </td>
                                        {
                                            columns.map((column, colIndex) => {
                                                return <td key={colIndex} className="px-6 py-4">{column.render(row)}</td>
                                            })
                                        }
                                    </tr>
                                )
                            })
                        }
                    </tbody>
                </table>
            </div>
        </>
    );
}

export default Datatable;
