import SearchField from "./components/Input/SearchField";


interface Testing {
    label: string;
    description: string;
    created_date: string;
}

interface Column<Row> {
    label: string;
    render: (row: Row) => JSX.Element | string
}

const rows: Testing[] = [
    { label: 'LAB 1', description: '1', created_date: '12/10/12'},
    { label: '', description: '2', created_date: '12/10/12'},
    { label: '', description: '3', created_date: '12/10/12'},
    { label: '', description: '4', created_date: '12/10/12'},
]

const columns: Column<Testing>[] = [
    {
        label: "Label",
        render: (row: Testing) => <p className="font-bold">{row.label}</p>,
    },
    {
        label: "Description",
        render: (row: Testing) => row.description,
    },
    {
        label: "Created",
        render: (row: Testing) => row.created_date,
    },
    {
        label: "Action",
        render: (row: Testing) => row.created_date,
    }
]

export default function App() {

    const onSearchQuery = (query: string) => {

    }

    return (
        <div className='h-screen w-screen flex-col items-center justify-center'>

            <div className="h-[64px] bg  w-screen flex items-center px-12 justify-between border">
                <h1 className="text-xl text-gray-800 font-bold">Template</h1>
            </div>

            <div className="p-12">
                <div className="text-xl font-semibold p-3.5 border flex justify-between items-center rounded-tr-lg rounded-tl-lg">
                    <div className="w-96">
                        <SearchField placeholder="Rechercher une ressource..." onChange={onSearchQuery}/>
                    </div>

                    <div className="text-sm font-semibold">
                        {rows.length} Résultats
                    </div>
                </div>
                <div className="relative overflow-x-auto shadow-md ">
                    <table className="w-full text-sm text-left text-gray-500 ">
                        <thead className="text-xs text-[#a9b3c0] uppercase bg-[#fdfdfd]  border">
                            <tr>
                                <th scope="col" className="p-4">
                                    <div className="flex items-center">
                                        <input id="checkbox-all-search" type="checkbox" className="w-4 h-4 text-blue-600 bg-gray-100 border-gray-300 rounded focus:ring-blue-500  focus:ring-2 "/>
                                        <label className="sr-only">checkbox</label>
                                    </div>
                                </th>
                                {
                                    columns.map((column) => (
                                        <th scope="col" className="px-6 py-3">
                                            {column.label}
                                        </th>
                                    ))
                                }
                                <th scope="col" className="px-6 py-3">
                                    Action
                                </th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                rows.map((row, index) => {
                                    return (
                                        <tr key={index} className="bg-white border-b">
                                            <td className="w-4 p-4">
                                                <div className="flex items-center">
                                                    <input  type="checkbox" className="w-4 h-4 text-blue-600 bg-gray-100 border-gray-300 rounded focus:ring-blue-500 dark:focus:ring-blue-600 dark:ring-offset-gray-800 dark:focus:ring-offset-gray-800 focus:ring-2 dark:bg-gray-700 dark:border-gray-600"/>
                                                    <label className="sr-only">checkbox</label>
                                                </div>
                                            </td>
                                            {
                                                columns.map((column, colIndex) => {
                                                    return <td key={colIndex} className="px-6 py-4">{column.render(row)}</td>
                                                })
                                            }
                                        </tr>
                                    )
                                })
                            }
                        </tbody>
                    </table>
                </div>
            </div>

        </div>
    );
}
