import ApexCharts from "apexcharts";
import React, { useEffect, useState } from "react";

const options = {
    series: [
      {
        name: "Leads",
        color: "#FDBA8C",
        data: [
          { x: "Mon", y: 232 },
          { x: "Tue", y: 113 },
          { x: "Wed", y: 341 },
          { x: "Thu", y: 224 },
          { x: "Fri", y: 522 },
          { x: "Sat", y: 411 },
          { x: "Sun", y: 243 },
        ],
      },
    ],
    chart: {
      type: "bar",
      fontFamily: "Inter, sans-serif",
      toolbar: {
        show: false,
      },
    },
    plotOptions: {
      bar: {
        horizontal: false,
        columnWidth: "70%",
        borderRadiusApplication: "end",
        borderRadius: 12,
      },
    },
    states: {
      hover: {
        filter: {
          type: "darken",
          value: 1,
        },
      },
    },
    stroke: {
      show: true,
      width: 0,
      colors: ["transparent"],
    },
    grid: {
      show: false,
      strokeDashArray: 4,
      padding: {
        left: 2,
        right: 2,
        top: -14
      },
    },
    dataLabels: {
      enabled: false,
    },
    legend: {
      show: false,
    },
    xaxis: {
      floating: false,
      labels: {
        show: true,
        style: {
          fontFamily: "Inter, sans-serif",
          cssClass: 'text-xs font-normal fill-gray-500 dark:fill-gray-400'
        }
      },
      axisBorder: {
        show: false,
      },
      axisTicks: {
        show: false,
      },
    },
    yaxis: {
      show: false,
    },
    fill: {
      opacity: 1,
    },
}


const Dashboard = () => {

    const ref = React.useRef(null);
    const [loaded, setLoaded] = useState<boolean>(false);

    useEffect(() => {

        if (loaded === false) {
            const chartInstance = new ApexCharts(ref.current, options)
            chartInstance.render()
            setLoaded(true)
        }
    }, [loaded])

    return <>
        <div className="max-w-sm w-full bg-white rounded-lg shadow dark:bg-gray-800 p-4 md:p-6">
            <div className="grid grid-cols-2">
                <dl className="flex items-center">
                    <dt className="text-gray-500 dark:text-gray-400 text-sm font-normal mr-1">Week:</dt>
                    <dd className="text-gray-900 text-sm dark:text-white font-semibold">$3,232</dd>
                </dl>
                <dl className="flex items-center justify-end">
                    <dt className="text-gray-500 dark:text-gray-400 text-sm font-normal mr-1">Conversion rate:</dt>
                    <dd className="text-gray-900 text-sm dark:text-white font-semibold">1.2%</dd>
                </dl>
            </div>

            <div id="column-chart" ref={ref}  key={options.series.length}>
            </div>
        </div>
    </>
}

export default Dashboard;
