    { id: '1', position: { x: 0, y: 0 }, data: { label: '1', description: 'Lalalala' }, type: 'customNode', dragHandle: '.custom-drag-handle' },
    { id: '2', position: { x: 0, y: 100 }, data: { label: '2', description: 'Lalalala' }, type: 'customNode', dragHandle: '.custom-drag-handle'  },
  ];
  const initialEdges = [{ id: 'e1-2', source: '1', target: '2' }];

  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);

  const onConnect = useCallback((params: any) => setEdges((eds) => addEdge(params, eds)), [setEdges]);
  const nodeTypes = useMemo(() => ({ customNode: CustomNode }), []);

  return (
    <>
        <FlowToolBar />
        <div className='h-[calc(100vh-64px)] w-full'>
            <ReactFlow
                nodes={nodes}
                edges={edges}
                onNodesChange={onNodesChange}
                onEdgesChange={onEdgesChange}
                onConnect={onConnect}
                nodeTypes={nodeTypes}
            >
                <Controls />
                <MiniMap />
                <Background gap={12} size={1} />
            </ReactFlow>
        </div>
    </>
  );
}
