import React from "react";

interface TabProps {
    options: string[]
    selected: string;
    onSelect?: (option: string) => void;
}

const Tabs: React.FC<TabProps> = ({options, selected, onSelect}) => {

    return <>
        <div className="text-sm font-medium text-center text-gray-500 border-b border-gray-200 dark:text-gray-400 dark:border-gray-700">
            <ul className="flex flex-wrap -mb-px space-x-2">
                {
                    options.map((option, key) => (
                        <li key={key} className="mr-2">
                            <button onClick={() => onSelect ? onSelect(option) : undefined} className={`inline-block p-4 border-b-2 ${option === selected ? 'border-orange-500' : ' border-transparent'} rounded-t-lg hover:text-gray-600 hover:border-orange-500`}>
                                {option}
                            </button>
                        </li>
                    ))
                }
            </ul>
        </div>
    </>
}


export default Tabs;
