interface SpanProps {
    label: string;
    color: 'blue' | 'green' | 'orange' | 'red';
}

const Span: React.FC<SpanProps> = ({label, color}) => {
    const colors = {
        "green": "bg-green-200 text-green-500  border-green-500",
        "red": "bg-red-200 text-red-500  border-red-500",
        "blue": "bg-blue-200 text-blue-500  border-blue-500",
    }

    return <>
        <span className={"px-3.5 py-1 border rounded-xl text-sm " + colors} >
            {label}
        </span>    
    </>
}

export default Span;
