import React, { useState } from 'react';
import { NavBar } from './components/NavBar';
import {DndContext, DragEndEvent, UniqueIdentifier} from '@dnd-kit/core';
import Draggable from './components/Draggable';
import Droppable from './components/Droppable';
// npm install @dnd-kit/core

function App() {

  const draggableContainers = ['A', 'B', 'C'];
  const [storedContainers, setStoredContainers] = useState<any>([])

  function handleDragEnd(event: DragEndEvent) {
    const {active, over} = event;

    if (over?.id === 'draft') {
      let copy = [...storedContainers, active.id]
      setStoredContainers(copy)
    }


  }

  return (
    <div className='h-screen w-screen'>
      <body>
          <div className=''>
            <DndContext onDragEnd={handleDragEnd}>
              <div className='flex h-full'>

                <div className='p-6 bg-white rounded-lg h-screen w-64 shadow'>
                  <h1 className='text-xl font-semibold mb-6'>To Drag</h1>

                  <div className='w-full h-full flex-col'>
                    {
                      draggableContainers.map((draggableContainer, index) => (
                        <Draggable id={draggableContainer} key={index}>
                          <div className='w-full border p-6'>
                            {draggableContainer}
                          </div>
                        </Draggable>
                      ))
                    }
                    </div>
                </div>

                <Droppable id="draft">
                  <div className='p-6 bg-white rounded-lg h-screen w-64 shadow space-y-2'>
                    <h1 className='text-xl font-semibold mb-6'>dropzone</h1>
                    {
                      storedContainers.map((storedContainer: any) => {
                        return (
                          <div className='w-full border p-6'>
                              {storedContainer}
                          </div>
                        )
                      })
                    }
                  </div>
                </Droppable>

              </div>
            </DndContext>
          </div>
      </body>
    </div>
  );
}

export default App;
